insert into tempuni(uni_name, uni_des, uni_img, min_point, seats_count) values ('Московский государственный университет', 'Московский государственный университет имени М. В. Ломоносова (с 1755 по 1917 год — Императорский Московский университет) — один из старейших и крупнейших классических университетов России, один из центров российской науки и культуры, расположенный в Москве. C 1940 года носит имя Михаила Васильевича Ломоносова. Полное название — федеральное государственное бюджетное образовательное учреждение высшего образования «Московский государственный университет имени М. В. Ломоносова».', 'img/mgu.jpg', 280, 90);
